import React from "react";

//Challange: Make an unordered list of people in order from youngest to oldest

const titleStyle = {
  fontSize: "20px",
  fontWeight: 800,
  textAlign: "center",
  margin: "100px 0",
  //   display: "flex",
  //   alignItems: "center",
  //   justifyContent: "center",
};

const displayStyle = {
  listStyle: "none",
  fontSize: "16px",
  marginBottom: "30px",
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
};

const arrayPeople = [
  { name: "Ben-0", age: 20 },
  { name: "Ben-1", age: 12 },
  { name: "Ben-2", age: 82 },
  { name: "Ben-3", age: 42 },
  { name: "Ben-4", age: 10 },
  { name: "Ben-5", age: 39 },
];

const sorted = arrayPeople.sort((a, b) => a.age - b.age);
// console.log(sorted);

const display = sorted.map((person) => {
  return (
    <li
      key={`${person.name}-${Math.floor(Math.random())}`}
      style={displayStyle}
    >
      <p>Name:{person.name}</p>
      <p>Age:{person.age}</p>
    </li>
  );
});

function SortAge() {
  return (
    <>
      <div style={titleStyle}>
        <p>
          Challange: Make an unordered list of people in order from youngest to
          oldest. Try to use sort(),map(), Math.floor(), Math.random()
        </p>
      </div>

      <div>
        <h1>{display}</h1>
      </div>
    </>
  );
}

export default SortAge;
