import logo from "./logo.svg";
// import "./App.css";

//Challange: Make an unordered list of people in order from youngest to oldest
const style = {
  listStyle: "none",
  fontSize: "16px",
  marginBottom: "30px",
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
};

const arrayPeople = [
  { name: "Ben-0", age: 20 },
  { name: "Ben-1", age: 12 },
  { name: "Ben-2", age: 82 },
  { name: "Ben-3", age: 42 },
  { name: "Ben-4", age: 10 },
  { name: "Ben-5", age: 39 },
];

const sorted = arrayPeople.sort((a, b) => a.age - b.age);
// console.log(sorted);

const display = sorted.map((person) => {
  return (
    <li key={`${person.name}-${Math.floor(Math.random())}`} className="list">
      <p className="text">Name:{person.name}</p>
      <p className="text">Age:{person.age}</p>
    </li>
  );
});

console.log(display);

function App() {
  return (
    <div className="App">
      <h1>{display}</h1>
    </div>
  );
}

export default App;
